    /// <summary>
    /// Classe pai da criptografia criada. As classes RSA, XOR e IMAGEM_RSA herdam dela.
    /// </summary>
    public class Crypto
    {



        // Métodos RSA -- explicações mais detalhadas dos métodos é dada na classe-filha RSA

        /// <summary>
        /// Responsável por encriptar a mensagem no estilo RSA.
        /// </summary>
        /// <param name="relatorio">Verifica se a encriptação ocorrerá na janela de relatório ou não.</param>
        /// <param name="msg_descriptada">Mensagem a ser encriptada (enviada pelo usuário).</param>
        /// <param name="chave_publica">Chave-pública (enviada pelo usuário).</param>
        public virtual string Encriptacao(bool relatorio, string msg_descriptada, int chave_publica) { return ""; }

        /// <summary>
        /// Responsável por descriptar a mensagem no estilo RSA
        /// </summary>
        /// <param name="relatorio">Verifica se a encriptação ocorrerá na janela de relatório ou não</param>
        /// <param name="msg_encriptada">Mensagem a ser descriptada (enviada pelo usuário)</param>
        /// <param name="chave_privada">Chave-privada (enviada pelo usuário)</param>
        public virtual string Decriptacao(bool relatorio, string msg_encriptada, int chave_privada) { return ""; }



    }

    // RSA 
    /// <summary>
    /// Classe usada para encriptar e descriptar as informações presentes na janela de Relatório e na de Criptação.
    /// </summary>
    /// <remarks>Herda da classe Crypto.</remarks>
    public sealed class RSA : Crypto
    {
        


        // Atributos

            // Mensagem - encriptada
            private string msg_encriptada = null; 
            private string msg_encriptada_partes; // captura a sequência numérica de cada char encriptado


            // Mensagem - descriptada
            private string msg_descriptada = null; 
            private string msg_cadeia_numerica; // cadeia de cinco caracteres da msg2
            private char msg_letras; // transforma cada sequência numérica em char, depois, vira string em msg_descriptada

            // Acessórios
            private int msg_ascii; // guarda a numeração decimal ASCII de cada caractere da mensagem descriptada, e o número de cinco dígitos da mensagem encriptada
            private BigInteger en; // armazena a operação de encriptação da cada char descriptado 
            private BigInteger de; // armazena a operação de descriptação de cada sequência numérica de cinco dígitos encriptada 

        // Propriedades
        
            private int Chave_mista { get; set; } // 667, 75067, 75067
            private int Chave_privada { get; set; } // 237, 17197, 35653
            private int Chave_publica { get; set; } // 13, 13, 997


        // Métodos

            // Encriptação

            /// <summary>
            /// Override do método Encriptação da classe Crypto.
            /// </summary>
            /// <param name="relatorio">
            /// Verifica se a encriptação ocorrerá na janela de relatório (true) ou na de criptação (false).
            /// Se ocorrer na de relatório, haverá algumas exceções, como:
            /// 1. A chave-públcia valerá sempre 13 (o usuário não precisa informá-la), enquanto que a mista será sempre 667;
            /// 2. O limite de 128 caracteres não será considerado.
            /// O resto, será o mesmo que a encriptação na janela de criptação.
            /// </param>
            /// <param name="msg_descriptada">Mensagem a ser encriptada (enviada pelo usuário).</param>
            /// <param name="chave_publica">Chave-pública (enviada pelo usuário).</param>
            /// <returns>Variável String (mensagem encriptada).</returns>
            public override string Encriptacao(bool relatorio, string msg_descriptada, int chave_publica)
            {
                // Atribuições:

                this.msg_descriptada = msg_descriptada;
                msg_encriptada = "";

                Chave_publica = (relatorio == false) ? chave_publica : 13;

                Chave_mista = ((this.msg_descriptada.Length <= 64) || (relatorio == true)) ? Acesso.chave_mista1 : Acesso.chave_mista2;



                // Processamento:
                
             
                // Se mensagem estar dentro do limite ou se for da janela de relatório, a encriptação ocorre.
                if ((this.msg_descriptada.Length <= 128 && relatorio == false) || (relatorio == true))
                {
                    foreach (char a in this.msg_descriptada)
                    {
                        msg_ascii = a; 

                        en = BigInteger.Pow(msg_ascii, Chave_publica) % Chave_mista;
                        msg_encriptada_partes = Convert.ToString(en);

                        /* 
                         * Transforma a string encriptada em cinco caracteres (cada letra) quando necessário.
                         * Isso acontece porque algumas operações em BigInteger en resultam em valores menores do que 10000.
                        */
                        while (msg_encriptada_partes.Length < 5)
                        {
                            msg_encriptada_partes = msg_encriptada_partes.Insert(0, "0");
                        }

                        msg_encriptada += msg_encriptada_partes;
                    
                    }
                }

                // Se mensagem não estar dentro do limite de 128 caracteres.
                else
                {
                    msg_encriptada = "Mensagem acima do limite de 128 caracteres.";
                }

                return msg_encriptada;
            }


            // Decriptação

            /// <summary>
            /// Override do método decriptação da classe Crypto.
            /// </summary>
            /// <param name="relatorio">
            /// /// Verifica se a encriptação ocorrerá na janela de relatório (true) ou na de criptação (false).
            /// Se ocorrer na de relatório, haverá algumas exceções, como:
            /// 1. A chave-mista será sempre 667;
            /// 2. O limite de 640 caracteres não será considerado.
            /// O resto, será o mesmo que a encriptação na janela de criptação.
            /// </param>
            /// <param name="msg_encriptada">Mensagem a ser descriptada (enviada pelo usuário).</param>
            /// <param name="chave_privada">Chave-privada (enviada pelo usuário).</param>
            /// <returns></returns>
            public override string Decriptacao(bool relatorio, string msg_encriptada, int chave_privada)
            {
                // Atribuições
                this.msg_encriptada = msg_encriptada;
                msg_descriptada = "";

                Chave_privada = chave_privada;

                Chave_mista = ((this.msg_encriptada.Length <= 320) || (relatorio == true)) ? Acesso.chave_mista1 : Acesso.chave_mista2;

                // Processamento
 
                // Se a mensagem estar dentro do limite ou se for na janela de relatório, a descriptação ocorre.
                if (((this.msg_encriptada.Length % 5 == 0) && (this.msg_encriptada.Length <= 640)) || (relatorio == true))
                {
                    for (int b = 1; b <= this.msg_encriptada.Length / 5; b++)
                    {
                        // Captura a cada sequência de cinco dígitos por caractere e transforma a String em Int32.
                        msg_cadeia_numerica = Convert.ToString(this.msg_encriptada[b * 5 - 5]) + Convert.ToString(this.msg_encriptada[b * 5 - 4]) + Convert.ToString(this.msg_encriptada[b * 5 - 3]) + Convert.ToString(this.msg_encriptada[b * 5 - 2]) + Convert.ToString(this.msg_encriptada[b * 5 - 1]);
                        msg_ascii = Convert.ToInt32(msg_cadeia_numerica);

                        // Processo de descriptação e conversão do número ascii para char / String.
                        de = BigInteger.Pow(msg_ascii, Chave_privada) % Chave_mista;
                        msg_letras = Convert.ToChar((int)de);
                        msg_descriptada += Convert.ToString(msg_letras);

                        // Eliminando o caractere padrão (?) - ele aparecia sempre que o número de cinco dígitos correspondia a nenhum char.
                        if (de % Chave_mista > 255)
                        /*  
                            * Na tabelas ASCII, cada caractere é representado por um número decimal entre 0 - 255, logo, números acima desse limite correspondem a nenhum char.
                            * Por padrão, quando um número acima do limite é encontrado, o compilador trata-o como o caractere de interrogação(?).
                            * Assim, como a mensagem encriptada é descriptada pela chave-mista na aritmética modular, informei ao compilador que o mesmo substitua a interrogação
                             pelo underline/underscore(_) sempre que o resultado da modulação seja maior que o limite(255).
                        */
                        {
                            msg_descriptada = msg_descriptada.Remove(b - 1); 
                            msg_descriptada = msg_descriptada.Insert(b - 1, "_"); 
                        }

                    }
                }

                // Se a mensagem estar fora do limite, uma mensagem de erro é exibida
                else
                {
                    // Fora do padrão de cinco dígitos para cada char.
                    if (this.msg_encriptada.Length % 5 != 0)
                    {
                        msg_descriptada = "Mensagem fora do padrão de sequência numérica."; 
                    }
                    // Acima de 640 caracteres
                    else
                    {
                        msg_descriptada = "Mensagem acima do limite de 640 caracteres.";
                    }

                }

                return msg_descriptada;
            }

        // Construtor
        /// <summary>
        /// Construtor da classe RSA. Por enquanto, ela faz nada de mais.
        /// </summary>
        public RSA()
        { }

        // Destruidor
        /// <summary>
        /// Destruidor da classe RSA. Por enquanto, ela faz nada de mais.
        /// </summary>
        ~RSA()
        { }
    }

    public class Imagem : Crypto
    {
        // Transformador de imagem para array de byte
        public byte[] Imagem_Para_Byte_Array(System.Drawing.Image imageIn)
        {
            
                MemoryStream imagem = new MemoryStream();
                imageIn.Save(imagem, System.Drawing.Imaging.ImageFormat.Jpeg); // converte imagem em array de bytes
                return imagem.ToArray();
            
        }

        // String para byte array
        public byte[] String_Para_Byte_Array(string hex)
        {
            
            return Enumerable.Range(0, hex.Length)
                             .Where(x => x % 2 == 0)
                             .Select(x => Convert.ToByte(hex.Substring(x, 2), 16))
                             .ToArray();
        }

        // byte array para imagem
        public Image Byte_Array_Para_Imagem(byte[] byteArrayIn)
        {
            MemoryStream byte_array = new MemoryStream(byteArrayIn);
            Image returnImage = Image.FromStream(byte_array);
            return returnImage;
        }
    }

